import { useState } from 'react';

const Modals = () => {
  const [isVisible, setIsVisible] = useState(false);

  function toggleModal() {
    setIsVisible(!isVisible);
  }
return {
    isVisible,
    toggleModal,
  }
};
export default Modals;
